export class Card {
  private _symbol: string;
  private _number: number;
  private _rank: string;

  constructor(symbol: string, number: number, rank: string) {
    this._symbol = symbol;
    this._number = number;
    this._rank = rank;
  }

  get symbol(): string {
    return this._symbol;
  }

  get number(): number {
    return this._number >= 11 ? 10 : this._number;
  }

  get rank(): string {
    return this._rank;
  }
}

import { Card } from "./Card";

export class Deck {
  private _cards: Card[];

  constructor() {
    this._cards = [];
    const symbols = [
      String.fromCodePoint(0x2660),
      String.fromCodePoint(0x2665),
      String.fromCodePoint(0x2666),
      String.fromCodePoint(0x2663),
    ];
    for (let i = 0; i < symbols.length; i++) {
      for (let j = 1; j <= 13; j++) {
        let number = j;
        if (j > 10) {
          number = 10;
        }
        let rank: string;
        if (j === 1) {
          rank = "A";
        } else if (j === 11) {
          rank = "J";
        } else if (j === 12) {
          rank = "Q";
        } else if (j === 13) {
          rank = "K";
        } else {
          rank = `${j}`;
        }
        this._cards.push(new Card(symbols[i], number, rank));
      }
    }
  }

  get cards(): Card[] {
    return this._cards;
  }

  set cards(cards: Card[]) {
    this._cards = cards;
  }

  shuffle(): void {
    const cardNum = this.cards.length;
    for (let i = cardNum - 1; i >= 0; i--) {
      const randomIndex = Math.floor(Math.random() * (i + 1));
      [this.cards[i], this.cards[randomIndex]] = [
        this.cards[randomIndex],
        this.cards[i],
      ];
    }
  }

  draw(): Card {
    return this.cards.pop();
  }
}

import * as colors from "colors";

import { Deck } from "./Deck";
import { Participant } from "./Participant";

export class Dealer extends Participant {
  private _isSecondCardOpen: boolean;

  constructor(deck: Deck) {
    super(deck);
    this._isSecondCardOpen = false;
  }

  get isSecondCardOpen(): boolean {
    return this._isSecondCardOpen;
  }

  set isSecondCardOpen(isSecondCardOpen: boolean) {
    this._isSecondCardOpen = isSecondCardOpen;
  }

  clear(): void {
    this.hand = [];
    this.isSecondCardOpen = false;
  }

  renderSecondCard(): void {
    this.isSecondCardOpen = true;

    let renderedCard = colors.bold("Dealer's second card: ");
    if (
      this.hand[1].symbol === String.fromCodePoint(0x2665) ||
      this.hand[1].symbol === String.fromCodePoint(0x2666)
    ) {
      renderedCard += colors.red.bgWhite(
        ` ${this.hand[1].symbol} ${this.hand[1].rank} `
      );
    } else {
      renderedCard += colors.black.bgWhite(
        ` ${this.hand[1].symbol} ${this.hand[1].rank} `
      );
    }
    renderedCard += "\n";
    console.log(renderedCard);
  }

  renderHand(): void {
    let renderedHand = colors.bold(`Dealer: `);
    if (this.isSecondCardOpen) {
      for (let i = 0; i < this.hand.length; i++) {
        if (
          this.hand[i].symbol === String.fromCodePoint(0x2665) ||
          this.hand[i].symbol === String.fromCodePoint(0x2666)
        ) {
          renderedHand += colors.red.bgWhite(
            ` ${this.hand[i].symbol} ${this.hand[i].rank} `
          );
        } else {
          renderedHand += colors.black.bgWhite(
            ` ${this.hand[i].symbol} ${this.hand[i].rank} `
          );
        }
        renderedHand += "  ";
      }
    } else {
      if (
        this.hand[0].symbol === String.fromCodePoint(0x2665) ||
        this.hand[0].symbol === String.fromCodePoint(0x2666)
      ) {
        renderedHand += colors.red.bgWhite(
          ` ${this.hand[0].symbol} ${this.hand[0].rank} `
        );
      } else {
        renderedHand += colors.black.bgWhite(
          ` ${this.hand[0].symbol} ${this.hand[0].rank} `
        );
      }
      renderedHand += "  ";
      renderedHand += colors.black.bgWhite(" ??? ");
    }
    renderedHand += "\n";
    console.log(renderedHand);
  }

  renderNewCard(): void {
    let renderedCard = colors.bold("Dealer's new card: ");
    if (
      this.hand[this.hand.length - 1].symbol === String.fromCodePoint(0x2665) ||
      this.hand[this.hand.length - 1].symbol === String.fromCodePoint(0x2666)
    ) {
      renderedCard += colors.red.bgWhite(
        ` ${this.hand[this.hand.length - 1].symbol} ${
          this.hand[this.hand.length - 1].rank
        } `
      );
    } else {
      renderedCard += colors.black.bgWhite(
        ` ${this.hand[this.hand.length - 1].symbol} ${
          this.hand[this.hand.length - 1].rank
        } `
      );
    }
    renderedCard += "\n";
    console.log(renderedCard);
  }
}

import { Card } from "./Card";
import { Deck } from "./Deck";

export class Participant {
  private _deck: Deck;
  private _hand: Card[];

  constructor(deck: Deck) {
    this._deck = deck;
    this._hand = [];
  }

  get deck(): Deck {
    return this._deck;
  }

  set deck(deck: Deck) {
    this._deck = deck;
  }

  get hand(): Card[] {
    return this._hand;
  }

  set hand(cards: Card[]) {
    this._hand = cards;
  }

  get sum(): number {
    let sum = this.hand.reduce((sum, card) => sum + card.number, 0);
    let aceNum = this.hand.filter((card) => card.number === 1).length;
    while (sum <= 11 && aceNum >= 1) {
      aceNum--;
      sum += 10;
    }
    return sum;
  }

  get isBlackjack(): boolean {
    return this.sum === 21 && this.hand.length === 2;
  }

  get isBusted(): boolean {
    return this.sum > 21;
  }

  deal(): void {
    this.hand.push(this.deck.draw());
    this.hand.push(this.deck.draw());
  }

  hit(): void {
    this.hand.push(this.deck.draw());
  }
}

import * as figlet from "figlet";
import * as readlineSync from "readline-sync";
import * as colors from "colors";

import { Card } from "./Card";
import { Deck } from "./Deck";
import { Dealer } from "./Dealer";
import { Player } from "./Player";

export class Game {
  private deck: Deck;
  private dealer: Dealer;
  private player: Player;

  constructor() {
    this.deck = new Deck();
    this.dealer = new Dealer(this.deck);
    this.player = new Player(this.deck);
  }

  async renderTitle(): Promise<void> {
    // タイトルのアスキーアートを表示する
    console.log(
      colors.rainbow(
        figlet.textSync("BLACK JACK", {
          font: "Big Chief",
          horizontalLayout: "default",
          verticalLayout: "default",
        })
      )
    );
    readlineSync.question(colors.bold("Please Enter to Start"));
    console.log("");
  }

  async inputBet(): Promise<void> {
    try {
      this.player.renderMoney();
      const input: string = readlineSync.question(
        `${colors.bold("Set Your Bet: ")}$`
      );
      this.player.setBet(input);
      console.log(colors.bold.yellow(`\nYour bet: $${this.player.bet}`));
      readlineSync.question(colors.bold("(Enter)"));
      console.log("");
    } catch (e) {
      console.log(e.message);
    }
  }

  async actByPlayer(): Promise<void> {
    const action = await this.player.selectAction();
    try {
      if (action === "h") {
        this.player.hit();
        this.player.hasHit = true;
        readlineSync.question(
          colors.bold.green("You hit.") + colors.bold("\n(Enter)")
        );
        console.log("");
        this.player.renderNewCard();
      } else if (action === "d" && !this.player.hasHit) {
        if (this.player.money - this.player.bet < 0) {
          throw new Error(
            colors.bold("You can't double down. You don't have enough money.")
          );
        }
        this.player.doubleDown();
        readlineSync.question(
          colors.bold.cyan("You doubled down.") + colors.bold("\n(Enter)")
        );
        console.log("");
        this.player.renderNewCard();
      } else if (action === "s") {
        this.player.isStanding = true;
        readlineSync.question(
          colors.bold.yellow("You stand.") + colors.bold("\n(Enter)")
        );
        console.log("");
      } else {
        throw new Error("\nPlease input Hit[h] or DoubleDown[d] or Stand[s]");
      }
      this.dealer.renderHand();
      this.player.renderHand();
      readlineSync.question(colors.bold("(Enter)"));
      console.log("");
    } catch (e) {
      console.log(e.message);
    }
  }

  checkResult(): void {
    if (this.dealer.isBusted) {
      this.player.money += 2 * this.player.bet;
      console.log(colors.bold.red("Dealer Busted"));
      console.log(colors.bold.red("You Win!!"));
      console.log(
        colors.bold.red("You won ") + colors.bold.red(`$${this.player.bet}`)
      );
    } else if (this.player.isBusted) {
      console.log(colors.bold.blue("You Busted"));
      console.log(colors.bold.blue("You Lose"));
      console.log(
        colors.bold.blue("You lost ") + colors.bold.blue(`$${this.player.bet}`)
      );
    } else if (this.dealer.sum === 21 && this.player.sum === 21) {
      if (
        (this.dealer.isBlackjack && this.player.isBlackjack) ||
        (!this.dealer.isBlackjack && !this.player.isBlackjack)
      ) {
        this.player.money += this.player.bet;
        console.log(colors.bold("Draw"));
      } else if (this.dealer.isBlackjack && !this.player.isBlackjack) {
        console.log(colors.bold.blue("You Lose"));
        console.log(
          colors.bold.blue("You lost ") +
            colors.bold.blue(`$${this.player.bet}`)
        );
      } else {
        this.player.money += Math.floor(2.5 * this.player.bet);
        console.log(colors.rainbow("B L A C K J A C K"));
        console.log(colors.bold.red("You Win!!"));
        console.log(
          colors.bold.red("You won ") +
            colors.bold.red(`$${1.5 * this.player.bet}`)
        );
      }
    } else if (this.player.isBlackjack) {
      this.player.money += Math.floor(2.5 * this.player.bet);
      console.log(colors.rainbow("B L A C K J A C K"));
      console.log(colors.bold.red("You Win!!"));
      console.log(
        colors.bold.red("You won ") +
          colors.bold.red(`$${1.5 * this.player.bet}`)
      );
    } else if (this.dealer.sum > this.player.sum) {
      console.log(colors.bold.blue("You Lose"));
      console.log(
        colors.bold.blue("You lost ") + colors.bold.blue(`$${this.player.bet}`)
      );
    } else if (this.dealer.sum < this.player.sum) {
      this.player.money += 2 * this.player.bet;
      console.log(colors.bold.red("You Win!!"));
      console.log(
        colors.bold.red("You won ") + colors.bold.red(`$${this.player.bet}`)
      );
    } else {
      this.player.money += this.player.bet;
      console.log(colors.bold("Draw"));
    }
    console.log("");
    this.player.renderMoney();
    console.log("");
  }

  async start(): Promise<void> {
    await this.renderTitle();

    while (this.player.money > 0) {
      this.deck.shuffle();
      this.dealer.deck = this.deck;
      this.player.deck = this.deck;
      this.dealer.clear();
      this.player.clear();

      while (this.player.bet === 0) {
        await this.inputBet();
      }

      this.dealer.deal();
      this.player.deal();
      this.dealer.renderHand();
      this.player.renderHand();

      while (
        !this.player.isBlackjack &&
        !this.player.isBusted &&
        !this.player.isStanding &&
        !this.player.hasDoubleDowned &&
        this.player.sum < 21
      ) {
        await this.actByPlayer();
      }

      if (!this.player.isBusted) {
        this.dealer.renderSecondCard();
        this.dealer.renderHand();
        this.player.renderHand();
        readlineSync.question(colors.bold("(Enter)"));
        console.log("");
      }

      while (
        ((this.dealer.sum === 17 &&
          this.dealer.hand.filter((card: Card) => card.rank === "A").length >=
            1) ||
          this.dealer.sum < 17) &&
        !this.player.isBusted
      ) {
        // ディーラーの手札の合計が17かつ手札にエースが含まれるとき、または17未満のとき、条件を満たさなくなるまで以下を繰り返す
        this.dealer.hit();
        this.dealer.renderNewCard();
        this.dealer.renderHand();
        this.player.renderHand();
        readlineSync.question(colors.bold("(Enter)"));
        console.log("");
      }
      this.checkResult();
      if (this.player.money > 0) {
        console.log(colors.bold("Please Enter to start next game"));
        readlineSync.question(colors.bold("(Enter)"));
        console.log("");
      } else {
        console.log(colors.bold("Game Over!!"));
      }
    }
  }
}

import * as readlineSync from "readline-sync";
import * as colors from "colors";

import { Deck } from "./Deck";
import { Participant } from "./Participant";

export class Player extends Participant {
  private _money: number;
  private _bet: number;
  private _hasHit: boolean;
  private _hasDoubleDowned: boolean;
  private _isStanding: boolean;

  constructor(deck: Deck) {
    super(deck);
    this._money = 1000;
    this._bet = 0;
    this._hasHit = false;
    this._hasDoubleDowned = false;
    this._isStanding = false;
  }

  get money(): number {
    return this._money;
  }

  set money(money: number) {
    this._money = money;
  }

  get bet(): number {
    return this._bet;
  }

  set bet(bet: number) {
    this._bet = bet;
  }

  get hasHit(): boolean {
    return this._hasHit;
  }

  set hasHit(hasHit: boolean) {
    this._hasHit = hasHit;
  }

  get hasDoubleDowned(): boolean {
    return this._hasDoubleDowned;
  }

  set hasDoubleDowned(hasDoubleDowned: boolean) {
    this._hasDoubleDowned = hasDoubleDowned;
  }

  get isStanding(): boolean {
    return this._isStanding;
  }

  set isStanding(isStanding: boolean) {
    this._isStanding = isStanding;
  }

  clear(): void {
    this.hand = [];
    this.bet = 0;
    this.hasHit = false;
    this.hasDoubleDowned = false;
    this.isStanding = false;
  }

  renderMoney(): void {
    console.log(
      colors.bold.yellow("Your money: ") + colors.bold.yellow(`$${this.money}`)
    );
  }

  setBet(input: string): void {
    if (
      isNaN(Number(input)) ||
      !Number.isInteger(Number(input)) ||
      Number(input) <= 0
    ) {
      throw new Error("\nPlease input a positive integer.");
    } else if (Number(input) > this.money) {
      throw new Error("\nPlease bet an amount of money you can.");
    } else {
      this.bet = Number(input);
    }
    this.money -= this.bet;
  }

  renderHand(): void {
    let renderedHand = colors.bold(`You:    `);
    for (let i = 0; i < this.hand.length; i++) {
      if (
        this.hand[i].symbol === String.fromCodePoint(0x2665) ||
        this.hand[i].symbol === String.fromCodePoint(0x2666)
      ) {
        renderedHand += colors.red.bgWhite(
          ` ${this.hand[i].symbol} ${this.hand[i].rank} `
        );
      } else {
        renderedHand += colors.black.bgWhite(
          ` ${this.hand[i].symbol} ${this.hand[i].rank} `
        );
      }
      renderedHand += "  ";
    }
    renderedHand += "\n";
    console.log(renderedHand);
  }

  renderNewCard(): void {
    let renderedCard = colors.bold("Your new card: ");
    if (
      this.hand[this.hand.length - 1].symbol === String.fromCodePoint(0x2665) ||
      this.hand[this.hand.length - 1].symbol === String.fromCodePoint(0x2666)
    ) {
      renderedCard += colors.red.bgWhite(
        ` ${this.hand[this.hand.length - 1].symbol} ${
          this.hand[this.hand.length - 1].rank
        } `
      );
    } else {
      renderedCard += colors.black.bgWhite(
        ` ${this.hand[this.hand.length - 1].symbol} ${
          this.hand[this.hand.length - 1].rank
        } `
      );
    }
    renderedCard += "\n";
    console.log(renderedCard);
  }

  doubleDown(): void {
    this.hasDoubleDowned = true;
    this.money -= this.bet;
    this.bet *= 2;
    this.hand.push(this.deck.draw());
  }

  selectAction(): string {
    const question = this.hasHit
      ? `${colors.bold("Select Your Action.")} ${colors.bold.green(
          "Hit[h]"
        )} ${colors.bold("/")} ${colors.bold.yellow("Stand[s]")}${colors.bold(
          ":"
        )} `
      : `${colors.bold("Select Your Action.")} ${colors.bold.green(
          "Hit[h]"
        )} ${colors.bold("/")} ${colors.bold.cyan(
          "DoubleDown[d]"
        )} ${colors.bold("/")} ${colors.bold.yellow("Stand[s]")}${colors.bold(
          ":"
        )} `;
    const answer = readlineSync.question(question);
    return answer;
  }
}
